SELECT c.id, c.training_institutions_id, c.cooperative_merchant_id, m.merchant_name, c.img_url, c.`name`, c.teachers, ct.`name` AS train_type, c.content, c.address, c.enrollment_required, c.phone, c.enrollment_read, c.create_date, c.recommend_time, c.recommend, c.recommend_level, c.`status`, ti.org_name FROM curriculum c, curriculum_type ct, training_institution ti, merchant m WHERE 1=1 and c.train_type = ct.`code` AND ti.id = c.training_institutions_id and c.cooperative_merchant_id = m.id
AND ti.org_name like concat('%',#{training_institutions_name},'%')
AND c.name like concat('%',#{name},'%')
AND ct.code = #{train_type}
ORDER BY c.create_date DESC
limit #{pNum},#{pSize}

SELECT COUNT(1) FROM curriculum c, curriculum_type ct WHERE c.train_type = ct.`code`
AND c.training_institutions_id = #{training_institutions_id}
AND c.name like concat('%',#{name},'%')
AND ct.code = #{train_type}

SELECT c.id, c.training_institutions_id, c.cooperative_merchant_id, c.img_url, c.`name`, c.teachers, ct.`name` AS train_type, c.content, c.address, c.enrollment_required, c.phone, c.enrollment_read, c.create_date, c.recommend_time, c.recommend, c.recommend_level, c.`status` FROM curriculum c, curriculum_type ct WHERE c.train_type = ct.`code`
AND c.training_institutions_id = #{training_institutions_id}
AND c.name LIKE CONCAT('%',#{name},'%')
AND c.id NOT IN
#{item}

