SELECT c.id, c.training_institutions_id, c.cooperative_merchant_id, c.img_url, c.`name`, c.teachers, ct.`name` AS train_type, c.content, c.address, c.enrollment_required, c.phone, c.enrollment_read, c.create_date, c.recommend_time, c.recommend, c.recommend_level, c.`status` FROM curriculum c, curriculum_type ct WHERE c.train_type = ct.`code`
AND c.training_institutions_id = #{training_institutions_id}
AND c.id NOT IN
#{item}

SELECT c.`name`, c.training_institutions_id, t.org_name, ct.`name` AS train_name, cc.`name` AS class_name, cc.lean_time, cc.leantime_type, co.student_name, co.phone, co.pay_time, cct.time, ifnull(co.pay_fee, '') pay_fee FROM curriculum_order co, curriculum c, curriculum_class cc, curriculum_class_times cct, curriculum_type ct, training_institution t WHERE co.curriculum_id = c.id AND co.curriculum_class_id = cc.id AND co.curriculum_class_time_id = cct.id AND c.train_type = ct.`code` and c.training_institutions_id=t.id and co.status = '1'
AND c.train_type=#{train_type}
AND c.training_institutions_id=#{training_institutions_id}
AND c.id=#{name}
AND cc.id=#{class_name}
AND co.phone like concat('%',#{phone},'%')
AND co.student_name like concat('%',#{student_name},'%')
limit #{pNum},#{pSize}

SELECT count(1) FROM curriculum_order co, curriculum c, curriculum_class cc, curriculum_class_times cct, curriculum_type ct, training_institution t WHERE co.curriculum_id = c.id AND co.curriculum_class_id = cc.id AND co.curriculum_class_time_id = cct.id AND c.train_type = ct.`code` and c.training_institutions_id=t.id and co.status = '1'
AND c.train_type=#{train_type}
AND c.training_institutions_id=#{training_institutions_id}
AND c.id=#{name}
AND cc.id=#{class_name}
AND co.phone like concat('%',#{phone},'%')
AND co.student_name like concat('%',#{student_name},'%')

select distinct a.id curriculum_id, a.name curriculum_name
from curriculum a
left join curriculum_discount b on a.id = b.curriculum_id
where 1=1
and a.training_institutions_id = #{institutionId}
and (
b.curriculum_id is null

  or a.id = #{curriculumId}

)

SELECT co.id AS id, co.order_number AS orderNumber, ct.`name` AS trainType, tr.org_name AS orgName, c.`name` AS curriculumName, cc.`name` AS curriculumClassName, co.create_time AS createTime, co.fee, ( CASE WHEN co.pay_fee IS NULL THEN '' ELSE co.pay_fee END ) AS payFee, co.come, co.phone, ( CASE co.`status` WHEN '0' THEN '未支付' WHEN '1' THEN '支付成功' WHEN '2' THEN '支付失败' WHEN '3' THEN '已退款' WHEN '4' THEN '失效订单' WHEN '5' THEN '异常订单' ELSE '' END ) AS `status`, ( CASE co.`re_status` WHEN '0' THEN '未确认' WHEN '1' THEN '已确认' ELSE '' END ) AS restatus FROM curriculum_order co LEFT JOIN curriculum c ON c.id = co.curriculum_id LEFT JOIN curriculum_class cc ON cc.id = co.curriculum_class_id LEFT JOIN curriculum_class_times cct ON cct.id = co.curriculum_class_time_id LEFT JOIN training_institution tr ON tr.id = c.training_institutions_id LEFT JOIN curriculum_type ct ON ct.`code` = c.train_type WHERE 1=1
AND co.phone = #{phone}
AND c.`name` like CONCAT('%',#{curriculumName},'%')
AND c.train_type = #{trainType}
AND co.create_time >=#{startDate}
AND co.create_time <= #{endDate}
AND UPPER(co.come) = UPPER(#{come})
AND co.status IN (#{status})
ORDER BY co.create_time DESC LIMIT #{startIndex},#{pageSize}

SELECT COUNT(0) FROM curriculum_order co LEFT JOIN curriculum c ON c.id = co.curriculum_id LEFT JOIN curriculum_class cc ON cc.id = co.curriculum_class_id LEFT JOIN curriculum_class_times cct ON cct.id = co.curriculum_class_time_id LEFT JOIN training_institution tr ON tr.id = c.training_institutions_id LEFT JOIN curriculum_type ct ON ct.`code` = c.train_type WHERE 1=1
AND co.phone = #{phone}
AND c.`name` like CONCAT('%',#{curriculumName},'%')
AND c.train_type = #{trainType}
AND co.create_time >=#{startDate}
AND co.create_time <= #{endDate}
AND UPPER(co.come) = UPPER(#{come})
AND co.status IN (#{status})

select t.id, t.name from curriculum t
where 1=1

    and t.status = #{status}


    and t.training_institutions_id = #{institution_id}

order by t.training_institutions_id, t.create_date

select t.id, t.name from curriculum_class t
where 1=1

    and t.status = #{status}


    and t.curriculum_id = #{curriculum_id}

order by t.curriculum_id
