mapper.select:
SELECT d.id AS id, dt.`name` AS discountType, m.`name` AS mainStadium, CONCAT(DATE(d.start_time),'—',DATE(d.end_time)) AS timeToTime, d.sub_stadium_id AS subStadiumId, ( CASE d.discount_channel WHEN '0' THEN '线下窗口' WHEN '1' THEN '线上支付' ELSE '' END ) AS discountChannel, p.payment AS payType, ( CASE d.`status` WHEN '0' THEN '未使用' WHEN '1' THEN '使用中' WHEN '2' THEN '已过期' ELSE '' END ) AS status FROM discount_configure d LEFT JOIN discount_type dt ON d.discount_type = dt.id LEFT JOIN main_stadium m ON m.id = d.main_stadium_id LEFT JOIN sub_stadium s ON s.id = d.sub_stadium_id LEFT JOIN payment p ON p.id = d.pay_type WHERE 1 = 1
AND d.main_stadium_id = #{mainStadiumId}
AND d.sub_stadium_id = #{subStadiumId}
AND d.status = #{status}
ORDER BY d.create_time DESC LIMIT #{startIndex},#{pageSize}

SELECT COUNT(0) FROM discount_configure d LEFT JOIN discount_type dt ON d.discount_type = dt.id LEFT JOIN main_stadium m ON m.id = d.main_stadium_id LEFT JOIN sub_stadium s ON s.id = d.sub_stadium_id LEFT JOIN payment p ON p.id = d.pay_type WHERE 1 = 1
AND d.main_stadium_id = #{mainStadiumId}
AND d.sub_stadium_id = #{subStadiumId}
AND d.status = #{status}

SELECT
d.id,count(0) AS count
FROM
discount_configure d
WHERE
1 = 1

    AND d.main_stadium_id = #{mainStadiumId}


    AND FIND_IN_SET(#{subStadiumId},d.sub_stadium_id)


    AND d.pay_type = #{payType}

AND d.`status` = '1'
AND (
(
d.start_time > #{startTime}
AND d.start_time < #{endTime}
)
OR (
d.start_time < #{startTime}
AND d.end_time > #{endTime}
)
OR (
d.end_time > #{startTime}
AND d.end_time < #{endTime}
)
)
