mapper.select:
SELECT ms.id,ms.name,ms.provincial_level,ms.city_level, ms.district_level,ms.trading_area,ms.address,ms.telephone, ms.open_time,ms.introduction,ms.lng,ms.lat,ms.classify,ms.is_special, ms.create_time, (case ms.status when '1' then '正常' WHEN '0' THEN '闭馆' WHEN '2' THEN '下线' ELSE '其他' END) as status FROM main_stadium ms
ms.NAME =#{name}
1 = 1
order by ms.create_time desc limit #{startIndex},#{endIndex}

SELECT count(*) from main_stadium ms



            ms.NAME =#{name}


            1 = 1

mapper.update:
update main_stadium
set name = #{name}, provincial_level = #{provincial_level}, city_level = #{city_level}, district_level = #{district_level}
where id = #{id}
