mapper.select:
SELECT ms.id,ms.name,p.province AS provincial_level,c.city as city_level, a.area AS district_level,ms.trading_area,ms.address,ms.telephone, ms.open_time,ms.introduction,ms.lng,ms.lat,ms.classify,ms.is_special, ms.create_time, (case ms.status when '1' then '正常' WHEN '0' THEN '闭馆' WHEN '2' THEN '下线' ELSE '其他' END) as status FROM main_stadium ms LEFT JOIN province p ON p.provinceID=ms.provincial_level LEFT JOIN city c ON c.cityID = ms.city_level LEFT JOIN area a ON a.areaID = ms.district_level
ms.NAME =#{name}
1 = 1
order by ms.create_time desc limit #{startIndex},#{endIndex}

SELECT count(*) from main_stadium ms



            ms.NAME =#{name}


            1 = 1

mapper.update:
update main_stadium
set name = #{name}, provincial_level = #{provincial_level}, city_level = #{city_level}, district_level = #{district_level},
trading_area = #{trading_area},address = #{address},telephone = #{telephone},
open_time = #{open_time},introduction = #{introduction},lng = #{lng},lat = #{lat},
classify = #{classify}
where id = #{id}

update main_stadium
set is_special = #{is_special}
where id = #{id}
