mapper.select:
SELECT id, ob.order_number AS orderNumber, c.categoryName, (case ob.ticket_type when '0' then '散票/年卡' WHEN '1' THEN '场地预定' WHEN '2' THEN '散客预定' ELSE '其他' END) as ticketType, ob.ticket_name AS ticketName, ob.stadium_name AS stadiumName, ob.sub_stadium_name AS subStadiumName, ob.order_time AS orderTime, ob.sell_price AS sellPrice, ob.cost_price AS costPrice, ob.order_channel AS orderChannel, ob.user_id AS userId, ob.user_name AS userName, ob.user_tel AS userTel, (case ob.status when '0' then '待支付' WHEN '1' THEN '待使用' WHEN '2' THEN '已使用' WHEN '3' THEN '支付失败' WHEN '5' THEN '失效订单' ELSE '其他' END) AS status FROM order_base_info ob LEFT JOIN classify c ON ob.classify = c.cid WHERE 1=1
AND user_id = #{userId}
AND ob.ticket_name like CONCAT('%',#{ticketName},'%')
AND ob.user_tel = #{userTel}
AND ob.order_time >=#{orderStartDate}
AND ob.order_time <= #{orderEndDate}
AND ob.ticket_type =#{ticketType}
AND ob.order_channel =#{orderChannel}
AND ob.status IN (#{status})
ORDER BY order_time DESC LIMIT #{startIndex},#{pageSize}

SELECT
  COUNT(0)
FROM
  order_base_info ob
WHERE 1=1

    AND user_id = #{userId}


    AND ob.user_tel = #{userTel}


    AND ob.ticket_name like CONCAT('%',#{ticketName},'%')


    AND
    ob.order_time >=#{orderStartDate}



    AND
    ob.order_time <= #{orderEndDate}


    AND
    ob.ticket_type =#{ticketType}


    AND
    ob.order_channel =#{orderChannel}


    AND
    ob.status IN (#{status})
