mapper.select:
SELECT count(*) FROM ( SELECT A.main_stadium_id, A.sub_stadium_id, A.ticket_strategy_id, A.classify, F.cid, F.categoryName FROM year_strategy_stadium_relations A LEFT JOIN classify F ON A.classify = F.cid WHERE A.main_stadium_id = #{mainStadiumId}
AND A.classify = #{classify}
AND A.sub_stadium_id = #{subStadiumId}
) E JOIN site_ticket_basic_info B ON E.ticket_strategy_id = B.id LEFT JOIN sub_stadium C ON E.sub_stadium_id = C.id LEFT JOIN main_stadium D ON C.parent_id = D.id
AND B.ticket_state = #{strategyState}
AND B.ticket_name LIKE concat('%', #{ticketName}, '%')

SELECT E.*, B.id AS yearStrategyId, B.ticket_name, B.refund_description AS refundDescription, B.order_description AS orderDescription, B.order_refund_rule AS orderRefundRule, C.id AS subStadiumId, C.`name` AS subStadiumName, D.id AS mainStadiumId, D.`name` AS mainStadiumName, B.create_time AS createTime, B.ticket_state AS strategy_state FROM ( SELECT A.main_stadium_id, A.sub_stadium_id, A.ticket_strategy_id, A.classify, F.cid, F.categoryName FROM year_strategy_stadium_relations A LEFT JOIN classify F ON A.classify = F.cid WHERE A.main_stadium_id = #{mainStadiumId}
AND A.classify = #{classify}
AND A.sub_stadium_id = #{subStadiumId}
) E JOIN site_ticket_basic_info B ON E.ticket_strategy_id = B.id LEFT JOIN `sub_stadium` C ON E.sub_stadium_id = C.id LEFT JOIN `main_stadium` D ON C.parent_id = D.id
AND B.ticket_state = #{strategyState}
AND B.ticket_name LIKE concat('%', #{ticketName}, '%')
ORDER BY B.create_time DESC LIMIT #{pageIndex},#{pageSize}

select id, ticket_Id AS ticketId, strategy_name AS strategyName, sub_stadium_id AS subStadium, site AS site, date_type AS dateType, week_details AS weekDetails, month_details AS monthDetails, specific_date AS specificDate, time_code AS timeCode, time_interval_id AS timeInterval, sell_price AS sellPrice, cost_price AS costPrice, store_price AS storePrice, create_time AS createTime, update_time AS updateTime FROM site_ticket_strategy_info
ticket_id = #{ticketId}
LIMIT #{pageIndex},#{pageSize}

SELECT time_inter as timeInter
FROM time_interval


        id = #{item}

SELECT display_name as fieldName
FROM field


        id = #{item}
