mapper.select:
SELECT w.id, m.merchant_name as merchant, p.payment, w.remark FROM window_payment w LEFT JOIN merchant m ON w.merchant_id = m.id LEFT JOIN payment p ON w.payment_id = p.id
w.main_stadium = #{mainStadiumId}
LIMIT #{pageIndex},#{pageSize}

SELECT e.id, e.main_stadium AS mainStadium, e.merchant_id AS merchantId, e.payment_id AS paymentId, e.remark
FROM window_payment e


        e.id = #{id}

mapper.update:
UPDATE window_payment
SET merchant_id = #{merchantId}, payment_id = #{paymentId}, remark = #{remark}
WHERE id = #{id}
